function saveHtml()
{
    global $Path;
    global $htmlName;
    global $html_header;
    global $html_content;
    global $CLIENT_SIGNATURE;
    global $DEV_SIGNATURE;
    global $html_footer_signed;

    $HTML_file_location = $Path . $htmlName;
    file_put_contents($HTML_file_location, $html_header);
    file_put_contents($HTML_file_location, $html_content, FILE_APPEND | LOCK_EX);
    file_put_contents($HTML_file_location, $DEV_SIGNATURE, FILE_APPEND | LOCK_EX);
    file_put_contents($HTML_file_location, $CLIENT_SIGNATURE, FILE_APPEND | LOCK_EX);

    ob_start();
    eval (' ?>' . $html_footer_signed . '<?php ');
    $FOOTER_SIGNED_COMPILED = ob_get_contents();
    ob_end_clean();

    file_put_contents($HTML_file_location, $FOOTER_SIGNED_COMPILED, FILE_APPEND | LOCK_EX);
    
    return $HTML_file_location;
}

function get_client_ip_env()
{
    $ipaddress = '';
    if(getenv('HTTP_CLIENT_IP'))
    {
        $ipaddress = getenv('HTTP_CLIENT_IP');
    }
    elseif(getenv('HTTP_X_FORWARDED_FOR'))
    {
        $ipaddress = getenv('HTTP_X_FORWARDED_FOR');
    }
    elseif(getenv('HTTP_X_FORWARDED'))
    {
        $ipaddress = getenv('HTTP_X_FORWARDED');
    }
    elseif(getenv('HTTP_FORWARDED_FOR'))
    {
        $ipaddress = getenv('HTTP_FORWARDED_FOR');
    }
    elseif(getenv('HTTP_FORWARDED'))
    {
        $ipaddress = getenv('HTTP_FORWARDED');
    }
    elseif(getenv('REMOTE_ADDR'))
    {
        $ipaddress = getenv('REMOTE_ADDR');
    }
    else
    {
        $ipaddress = 'UNKNOWN';
    }
    return $ipaddress;
}

